#define mVersion "M5Unified 1.0"

#include "main.h"
#define RECV_BUFFER 65536

void _drawJpg(uint8_t* buf, int size) {
	if(size<4) return;
	int x = GetL16(buf+0);
	int y = GetL16(buf+2);
	M5.Display.drawJpg(buf+4, size-4, x, y);
}

scratch3constructor:
		this.enabledBitmap = {Display:(1<<0), In_I2C:(1<<1), Ex_I2C:(1<<2), Imu:(1<<3),
							Speaker:(1<<4), Mic:(1<<5), Touch:(1<<6), Rtc:(1<<7),
							Axp192:(1<<8), Ip5306:(1<<9), Axp2101:(1<<10)};
		this.width = 320;
		this.height = 240;
		this.board = 0;
		this.enabled = 0;
		this.portSDA = 0;
		this.portSCL = 0;

		this.accel = [0,0,0];
		this.gyro = [0,0,0];

