scratch3funcs:
	getIMU(args, util) {
		let index = Number(args.ARG1);
		switch(index) {
		case 0:
		case 3:
			break;
		case 1:
		case 2:
			return this.accel[index-0];
		case 4:
		case 5:
			return this.gyro[index-3];
		default:
			return 0;
		}

		const _this = this;
		return this.sendRecv('_getIMUs', args)
		.then(result => {
			if(typeof result !== 'object') return result;
			if(result.length < 4*3) return result;
			let tmp2 = new DataView(result.buffer);
			let i;
			if(index == 0) {
				for(i = 0; i < 3; i++)
					_this.accel[i] = tmp2.getFloat32(i*4, true);
				return _this.accel[0];
			} else {
				for(i = 0; i < 3; i++)
					_this.gyro[i] = tmp2.getFloat32(i*4, true);
				return _this.gyro[0];
			}
		})
	}

	getConfig(args, util) {
		const boards = ['unknown', 'M5Stack', 'M5StackCore2', 'M5StickC',			// 0-3
						'M5StickCPlus', 'M5StackCoreInk', 'M5Paper', 'M5Tough',		// 4-7
						'M5Station', 'M5StackCoreS3', 'M5AtomS3', 'M5Atom',			// 8-11
						'M5AtomPsram', 'M5AtomU', 'M5StampPico', 'M5StampC3',		// 12-15
						'M5StampC3U', 'M5AtomS3Lite',								// 16
						];

		const _this = this;
		return this.sendRecv('_getConfig', {})
		.then(result => {
			if(typeof result !== 'object') return result;
			_this.width		= result[0] + (result[1]<<8);
			_this.height	= result[2] + (result[3]<<8);
			_this.board		= result[4] + (result[5]<<8);
			_this.enabled	= result[6] + (result[7]<<8);
			_this.portSDA	= result[8];
			_this.portSCL	= result[9];

			_this.comlib._runtime.renderer.setDevSize(_this.width, _this.height);

			let str = (_this.board < boards.length ? boards[_this.board]: 'invalid') + '(';
			if(_this.enabled & _this.enabledBitmap.Display)	str += 'LCD,';
			if(_this.enabled & _this.enabledBitmap.Imu)		str += 'IMU,';
			if(_this.enabled & _this.enabledBitmap.Speaker)	str += 'SPK,';
		//	if(_this.enabled & _this.enabledBitmap.Mic)		str += 'MIC,';
		//	if(_this.enabled & _this.enabledBitmap.Touch)	str += 'TOUCH,';
		//	if(_this.enabled & _this.enabledBitmap.Rtc)		str += 'RTC,';
			if(_this.enabled & _this.enabledBitmap.Axp192)	str += 'PWR,';
			if(_this.enabled & _this.enabledBitmap.Ip5306)	str += 'PWR,';
			if(_this.enabled & _this.enabledBitmap.Axp2101)	str += 'PWR,';
			str += ')';
			return str;
		})
	}

	getConfigParam(args) {
		if(args.ARG1 == 'SDA') return this.portSDA;
		if(args.ARG1 == 'SCL') return this.portSCL;
		return 0;
	}

	drawStage(args,util) {
		const tmpData = this.comlib._runtime.renderer.drawWithMask(util.sequencer.runtime.ioDevices.video._skinId,
								[-240,240], [-180,180], 0);
		if(typeof tmpData === 'undefined') return;
		const tmpData2 = Base64Util.base64ToUint8Array(tmpData.base64);
		args.ARG1 = new Uint8Array(4+tmpData2.length);
		args.ARG1.set([tmpData.x, tmpData.x>>8, tmpData.y, tmpData.y>>8], 0)
		args.ARG1.set(tmpData2, 4);
		console.log('size='+args.ARG1.length);

		return this.sendRecv('drawJpg', args);
	}

	drawStage2(args,util) {
		const maskSkinId = (args.ARG6 == '1') ? -1 : util.sequencer.runtime.ioDevices.video._skinId;
		const tmpData = this.comlib._runtime.renderer.drawWithMask(maskSkinId,
								[Number(args.ARG1),Number(args.ARG3)], 
								[Number(args.ARG2),Number(args.ARG4)], 
								Number(args.ARG5));
		if(typeof tmpData === 'undefined') return;
		const tmpData2 = Base64Util.base64ToUint8Array(tmpData.base64);
		const args2 = {ARG1:new Uint8Array(4+tmpData2.length)};
		args2.ARG1.set([tmpData.x, tmpData.x>>8, tmpData.y, tmpData.y>>8], 0)
		args2.ARG1.set(tmpData2, 4);
	//	const args2 = {ARG1:Base64Util.base64ToUint8Array(tmpData)}
		console.log('size='+args2.ARG1.length);

		return this.sendRecv('drawJpg', args2);
	}
